            <tr>
                <td>{{ materiel.id_Mat }}</td>
                <td>{{ materiel.libelle }}</td>
                <td>{{ materiel.type }}</td>
                <td>{{ materiel.prixAchat }}</td>
                <td>
                    <a href=\"{{ path('app_materiel_controller_back_edit', {'id_mat': materiel.id_mat}) }}\">edit</a>
                   
                    
